package math;

class IsSubsequence {
    public static void main(String[] args) {
        System.out.println("case 1 (expected true):" + isSubsequence("abc", "ahbgdc")); 
        System.out.println("case 2 (expected false): "+ isSubsequence("axc", "ahbgdc"));
        System.out.println("case 3 (expected false): " + isSubsequence("cba", "ahbgdc"));
        System.out.println("case 4 (expected true): " + isSubsequence("", "abcde"));
        System.out.println("case 5 (expected true): " + isSubsequence("abc", "abcdef"));
        
        System.out.println("case 7 (expected true): " + isSubsequence("ab", "baab"));
        System.out.println("case 8 (expected true): " + isSubsequence("leeeeetcode", ""));
    }

    public static boolean isSubsequence(String s, String t) {
        int i = 0, j = 0;

        while (i < s.length() && j < t.length()) {
            if (s.charAt(i) == t.charAt(j++)) {
                i++;
            }
        }
        return i == s.length();
    }
}

